select
from B_G_011 B left join T_USER ZU on B.ZDRID = ZU.ID left join T_USER SU on B.SXRID = SU.ID left join T_USER XU on B.SXJYRID = XU.ID where 1=1 and B.ZT in(2,3)
order by B.ZXBS asc,B.WXFABH desc,B.BB desc
order by ${pagination.sort} ${pagination.order}

select
,B1.MAXBB from B_G_011 B left join T_USER ZU on B.ZDRID = ZU.ID left join T_USER SU on B.SXRID = SU.ID left join T_USER XU on B.SXJYRID = XU.ID left join (select WXFABH,MAX(BB) AS MAXBB from B_G_011 where ZT != 8
and dprtcode = #{dprtcode}
group by WXFABH) B1 on B.WXFABH = B1.WXFABH where 1=1 and B.ZT != 8
order by B.WXFABH ${pagination.order},B.BB ${pagination.order}
order by ${pagination.sort} ${pagination.order}

