mapper.select:
select
,T1.ID as C_ID, T1.HTLSH as C_HTLSH, T1.HTQDRQ as C_HTQDRQ, T1.GYSMC as C_GYSMC from B_H_00401 T left join B_H_004 T1 on T.MAINID = T1.ID left join B_H_00201 T2 on T.TDDHCID = T2.ID where T.ZT = 1 and T1.DPRTCODE=#{paramsMap.dprtcode,jdbcType=VARCHAR} and T1.ZT in (2,9,10) and T1.HTLX = 1
and T2.BJID = #{paramsMap.bjid,jdbcType=VARCHAR}
and T2.BJH = #{paramsMap.bjh,jdbcType=VARCHAR}
order by T1.HTQDRQ desc
order by ${pagination.sort} ${pagination.order}

mapper.update:
update B_H_00401
  set DHSL = DHSL + #{dhsl, jdbcType=DECIMAL}
