mapper.select:
select
from D_015 D where D.ZT = 1 and (D.SQKSSJ is null or trunc(D.SQKSSJ) <= trunc(sysdate) ) and (D.SQJSSJ is null or trunc(D.SQJSSJ) >= trunc(sysdate))
and D.GYSLB = #{gyslb}
and D.DPRTCODE = #{dprtcode}

select D015.ID,D015.GYSBM,D015.GYSMC,D015.GYSLB,D013.BJ_CLF,D013.BJ_GSF,D013.BJ_QTF,D013.YXQ_KS,D013.YXQ_JS from D_015 D015 left join (select D0.GYSID,D0.BJ_CLF,D0.BJ_GSF,D0.BJ_QTF,D0.YXQ_KS,D0.YXQ_JS from D_013 D0 where D0.ZT = 1
and D0.BJLB = #{gyslb}
and D0.BJID = #{paramsMap.bjid}
and D0.DPRTCODE = #{dprtcode} and D0.YXQ_KS <= sysdate and (D0.YXQ_JS > sysdate or D0.YXQ_JS is null)) D013 on D015.ID = D013.GYSID where D015.DPRTCODE = #{dprtcode}
and D015.GYSLB = #{gyslb}
and D015.ZT = 1 and (D015.SQKSSJ is null or trunc(D015.SQKSSJ) <= trunc(sysdate) ) and (D015.SQJSSJ is null or trunc(D015.SQJSSJ) >= trunc(sysdate))
order by D015.GYSBM ${pagination.order},ID ASC
order by ${pagination.sort} ${pagination.order},ID ASC

mapper.sql:
	 
	
	    and (
	    	UPPER(D.GYSBM) like UPPER('%'||#{keyword}||'%')
	    	or UPPER(D.GYSMC) like UPPER('%'||#{keyword}||'%')
	    )
	
	
	    and D.GYSLB = #{gyslb}
	
	
	    and D.DPRTCODE = #{dprtcode}
	
	 
	
     and D.SQKSSJ <= to_date(#{paramsMap.currentDate, jdbcType=VARCHAR},'YYYY-MM-DD HH24:MI:SS')
    and D.SQJSSJ >= to_date(#{paramsMap.currentDate, jdbcType=VARCHAR},'YYYY-MM-DD HH24:MI:SS')

update.foreach:
UPDATE D_015
GYSLB = #{item.gyslb,jdbcType=VARCHAR}, GYSBM = #{item.gysbm,jdbcType=VARCHAR}, GYSMC = #{item.gysmc,jdbcType=VARCHAR}, GYSJC = #{item.gysjc,jdbcType=VARCHAR}, DZ = #{item.dz,jdbcType=VARCHAR}, PZH = #{item.pzh,jdbcType=VARCHAR}, SQKSSJ = #{item.sqkssj,jdbcType=TIMESTAMP}, SQJSSJ = #{item.sqjssj,jdbcType=TIMESTAMP}, SQFW = #{item.sqfw,jdbcType=VARCHAR}, ZSSM = #{item.zssm,jdbcType=VARCHAR}, BZ = #{item.bz,jdbcType=VARCHAR}, ZT = #{item.zt,jdbcType=DECIMAL}, CJRID = #{item.cjrid,jdbcType=VARCHAR}, CJSJ = current_timestamp
where ID = #{item.id,jdbcType=VARCHAR};

