mapper.select:
select b1.id, b1.ckdh,b1.cklx,(select t_user.username || ' ' || t_user.realname from t_user where t_user.id = b1.sqrid) sqr, to_char(b1.sqsj, 'yyyy-mm-dd hh24:mi:ss') sqsj ,b1.fjzch, b2.jddxms,b1.jdfzr,b1.sgbs,b1.zt,(select t_user.username || ' ' || t_user.realname from t_user where t_user.id = b1.cukid) ckr, to_char(b1.cksj, 'yyyy-mm-dd hh24:mi:ss') cksj,(select t_user.username || ' ' || t_user.realname from t_user where t_user.id = b1.zdrid) zdri, to_char(b1.zdsj, 'yyyy-mm-dd hh24:mi:ss') zdsj,b1.bz,b1.dprtcode from B_H_013 b1 left join b_h_014 b2 on b1.jddxid=b2.id
order by b1.zdsj DESC
order by ${pagination.sort} ${pagination.order}

mapper.sql:
	   where 1=1
	    
             and b1.zt = #{zt}
         
	    
             and b1.id = #{id}
         
 	
   
   
        and  b1.fjzch =#{fjzch}
   
	    
             and b1.cklx = #{cklx}
         
           
		    and b1.sqsj >=to_date(#{fxDateBegin}||' 00:00:00','YYYY-MM-DD HH24:MI:SS') 
		
         
		    and b1.sqsj <=to_date(#{fxDateEnd}||' 23:59:59','YYYY-MM-DD HH24:MI:SS') 
		
         
             and b1.dprtcode =  #{dprtcode}
         
         
		      and (UPPER(b1.id) like UPPER('%'||#{keyword}||'%')
		      or UPPER(b1.ckdh) like UPPER('%'||#{keyword}||'%')
		     )              
		  
		   
    	                                                                   
		    	                       
		    		 AND exists 
		    		( SELECT 1 from D_007 where 
		    		b1.fjzch = FJZCH
		    		AND b1.dprtcode = DPRTCODE
		    		AND zt = 1
		    		)

