mapper.select:
select 

from D_00901
where  ZT=1 AND CKH = #{0,jdbcType=VARCHAR} AND KWH = #{1,jdbcType=VARCHAR} and DPRTCODE = #{2,jdbcType=VARCHAR}

mapper.insert:
MERGE INTO D_00901 D1 USING (
SELECT #{item.id,jdbcType=VARCHAR} AS ID , #{item.mainid,jdbcType=VARCHAR} AS MAINID, #{item.kwh,jdbcType=VARCHAR} AS KWH, #{item.dprtcode,jdbcType=VARCHAR} AS DPRTCODE, #{item.ckh,jdbcType=VARCHAR} AS CKH, #{item.cklb,jdbcType=INTEGER} AS CKLB, #{item.bz,jdbcType=VARCHAR} AS BZ, #{item.bmid,jdbcType=VARCHAR} AS BMID, #{item.cjrid,jdbcType=VARCHAR} AS CJRID FROM dual
) D2 ON ( D1.MAINID=D2.MAINID AND D1.KWH=D2.KWH ) WHEN MATCHED THEN UPDATE SET D1.BZ=D2.BZ , D1.ZT=1 WHEN NOT MATCHED THEN INSERT (ID, MAINID, KWH, DPRTCODE, CKH, CKLB, BZ, ZT, BMID, CJRID, CJSJ) VALUES (D2.ID, D2.MAINID, D2.KWH, D2.DPRTCODE, D2.CKH, D2.CKLB, D2.BZ, 1, D2.BMID, D2.CJRID, SYSDATE)

mapper.select:
