mapper.select:
select id, dprtcode, jh, xlh, fjzch, az_fxjldid, az_jld, to_char((CASE WHEN az_zxrq IS NULL THEN az_sj ELSE az_zxrq END), 'yyyy-mm-dd') az_zxrq, az_gzzid, (select t_user.username || ' ' || t_user.realname from t_user where t_user.id = b_h_01002.az_gzzid) az_gzzxm, az_bz, cj_fxjldid, cj_jld, to_char((CASE WHEN cj_zxrq IS NULL THEN cj_sj ELSE cj_zxrq END), 'yyyy-mm-dd') cj_zxrq, cj_gzzid, (select t_user.username || ' ' || t_user.realname from t_user where t_user.id = b_h_01002.cj_gzzid) cj_gzzxm, cj_bz, cj_zsjjh, cj_zsjxlh, is_cx from b_h_01002
order by whsj desc
order by ${pagination.sort} ${pagination.order}

