mapper.select:
select s2013.bldh, s2013.gzms, case when (s2013.zc_sqrq is not null and s2013.zc_sqrq < (last_day(to_date(#{yf,jdbcType=VARCHAR}, 'yyyy-mm')) + 1)) then (to_char(s2013.zc_sqrq, 'yyyy-mm-dd')||'#__#'||s2013.zc_blyy||'#__#'||s201301.zc_qx) else (to_char(s2013.sc_sqrq, 'yyyy-mm-dd')||'#__#'||s2013.blyy||'#__#'||s201301.sc_qx) end as detail from b_s2_013 s2013 left join ( select mainid, wm_concat( case jd when 1 then jklbh||'#_#'||jkz else null end) sc_qx, wm_concat( case jd when 2 then jklbh||'#_#'||jkz else null end) zc_qx from b_s2_01301 s201301 group by s201301.mainid ) s201301 on s201301.mainid = s2013.id where s2013.dprtcode = #{dprtcode,jdbcType=VARCHAR} and ((s2013.sc_sqrq < (last_day(to_date(#{yf,jdbcType=VARCHAR}, 'yyyy-mm')) + 1) and s2013.sc_sqrq >= to_date(#{yf,jdbcType=VARCHAR} || '-01', 'yyyy-mm-dd')) or (s2013.zc_sqrq < (last_day(to_date(#{yf,jdbcType=VARCHAR}, 'yyyy-mm')) + 1) and s2013.zc_sqrq >= to_date(#{yf,jdbcType=VARCHAR} || '-01', 'yyyy-mm-dd'))) and s2013.zt in (4, 10) and s2013.fjzch = #{fjzch,jdbcType=VARCHAR}
union all
select s2012.bldh, s2012.qxms, to_char(s2012.sqrq, 'yyyy-mm-dd')||'#__#'||''||'#__#'||'1_10'||'#_#'||to_char(s2012.xfqx, 'yyyy-mm-dd') from b_s2_012 s2012 where s2012.dprtcode = #{dprtcode,jdbcType=VARCHAR} and s2012.sqrq < (last_day(to_date(#{yf,jdbcType=VARCHAR}, 'yyyy-mm')) + 1) and s2012.sqrq >= to_date(#{yf,jdbcType=VARCHAR} || '-01', 'yyyy-mm-dd') and s2012.zt in (4, 10) and s2012.fjzch = #{fjzch,jdbcType=VARCHAR}
