SELECT G2011.ID ID, G2011.DPRTCODE, G2011.JX, G201102.WXXMID, G201102.RWH, G2012.WXXMLX, G201102.GKID, G201102.GKBH, G2012.BB WXXMBB, G2012.CKH, G2012.RWMS, G2012.ZJH, G2012.EOBH, D005.ZWMS ZJH_ZWMS, D005.YWMS ZJH_YWMS, G201101.DLBH, G201101.DLZWMS DLBH_ZWMS, G201101.DLYWMS DLBH_YWMS, CASE WHEN G2012.SYX = '00000' THEN '00000' WHEN G2012.SYX = '-' THEN G201203.FJZCH ELSE '' END SYX FROM B_G2_011 G2011 --维修方案 JOIN B_G2_01102 G201102 --维修方案生效区-维修项目关系 ON G2011.DPRTCODE = #{dprtcode,jdbcType=VARCHAR} AND G2011.ID = G201102.MAINID AND G2011.ZXBS = 2
AND G2011.JX = #{jx,jdbcType=VARCHAR}
JOIN B_G2_012 G2012 --维修项目主表 ON G2012.YXBS = 1 AND G2012.ID = G201102.WXXMID LEFT JOIN D_005 D005 --ata章节 ON D005.DPRTCODE = G2012.DPRTCODE AND D005.ZJH = G2012.ZJH LEFT JOIN B_G2_01101 G201101 --维修项目大类 ON G201101.ID = G2012.WXXMDLID LEFT JOIN (SELECT G201203.MAINID WXXMID, WM_CONCAT(G201203.FJZCH) FJZCH FROM B_G2_01203 G201203 JOIN B_G2_012 G2012_1 ON G2012_1.DPRTCODE = #{dprtcode,jdbcType=VARCHAR}
AND G2012_1.JX =#{jx,jdbcType=VARCHAR}
AND G2012_1.ID = G201203.MAINID GROUP BY G201203.MAINID) G201203 ON G201203.WXXMID = G2012.ID WHERE 1 = 1
AND G2011.JX=#{jx,jdbcType=VARCHAR}
AND ( UPPER(G201102.RWH) LIKE UPPER('%'||#{paramsMap.keyword}||'%') OR UPPER(G2012.CKH) LIKE UPPER('%'||#{paramsMap.keyword}||'%') OR UPPER(G2012.RWMS) LIKE UPPER('%'||#{paramsMap.keyword}||'%') )
AND WXXMLX IN
#{item}
AND 1 = 0
ORDER BY G2012.ZJH, G2012.RWH
ORDER BY G201101.XC, G201101.DLBH, G2012.RWH
