where 1=1
and t1.id = #{id}
and t1.fjzch = #{fjzch}
and t1.DPRTCODE = #{dprtcode}
and ( UPPER(t1.zjh) like UPPER('%'||#{keyword}||'%') or UPPER(t1.zwmc) like UPPER('%'||#{keyword}||'%') or UPPER(t1.ywmc) like UPPER('%'||#{keyword}||'%') or UPPER(t1.jh) like UPPER('%'||#{keyword}||'%') or UPPER(t1.xlh) like UPPER('%'||#{keyword}||'%') or UPPER(t1.tsn) like UPPER('%'||#{keyword}||'%') or UPPER(t1.tso) like UPPER('%'||#{keyword}||'%'))
and t1.sxzt = 1 and t1.kzlx in (1, 2) and t1.zt = 1
