mapper.select:
select
,ZDR.ID as ZDR_ID,ZDR.USERNAME as ZDR_USERNAME,ZDR.REALNAME as ZDR_REALNAME ,B10.USERNAME || ' ' || B10.REALNAME AS GBR_ID ,(select count(1) from D_011 where D_011.mainid=B.ID and D_011.yxzt=1) as ATTACHCOUNT ,(select count(1) from b_G2_998 where b_G2_998.ywid=B.id and b_G2_998.zt=1 and b_G2_998.IS_JS=1) as YY ,(select count(1) from b_G2_998 where b_G2_998.ywid=B.id and b_G2_998.zt=1 ) as ZH ,DE.DPRTCODE OR_DPRTCODE ,DE.DPRTNAME OR_DPRTNAME from B_G2_010 B LEFT JOIN T_USER ZDR ON B.WHRID = ZDR.ID LEFT JOIN T_USER B10 ON B.GBRID= B10.ID LEFT JOIN T_DEPARTMENT DE ON B.DPRTCODE=DE.ID where 1 = 1
and B.ID = #{id}
order by B.WHSJ DESC,B.BB DESC, B.ID
order by ${pagination.sort} ${pagination.order}

choose.when:
                       
	    		 AND exists 
	    		( SELECT 1 from V_ROLE_JX where 
	    			B.jx= FJJX
	    		AND B.dprtcode = DPRTCODE
	    		AND USER_ID = #{paramsMap.userId,jdbcType=VARCHAR}
	    		)

mapper.select:
select 

from B_G2_010 B
where 1=1 and B.ID = #{id,jdbcType=VARCHAR}

select 
	max(B.Bb) AS BB
from B_G2_010 B
where 1=1 and B.EOBH = #{eobh,jdbcType=VARCHAR} 
		   and B.DPRTCODE = #{dprtcode,jdbcType=VARCHAR}

	 SELECT BG010.ID, --EO ID
     BG010.EOBH, --EO编号
     BG010.EOZT --EO主题
     FROM B_G2_010 BG010
     WHERE BG010.DPRTCODE =#{dprtcode,jdbcType=VARCHAR}
     AND BG010.JX =#{jx,jdbcType=VARCHAR}
     AND BG010.ZT = 7
     AND BG010.ZXBS = 2
     AND BG010.EOBH = #{eobh,jdbcType=VARCHAR}
