SELECT B1.ID,B1.DPRTCODE,B1.PGDH,B1.BB,B1.LYLX,B1.LYSM,B1.JSWJID,B1.GLPGDID,B1.JX,B1.PGQX,B1.ZJH,B1.PGDZT,B1.SXYQ,B1.BZ,B1.ZXBS, B1.F_BBID,B1.B_BBID,B1.ZT,B1.PGBMID,B1.PGRID,B1.PGSJ,B1.SHBMID,B1.SHRID,B1.SHSJ,B1.SHYJ,B1.PFBMID,B1.PFRID,B1.PFSJ,B1.PFYJ, B1.GBRID,B1.GBRQ,B1.GBYY,B1.PFJL,B1.SHJL,B2.MAINID, B2.WCLX,B2.DJ,B2.JJCD,B2.SJGZ,B2.WJZY,B2.SYFW_YWJ,B2.SYX,B2.FSYYY,B2.SYLB,B2.SYFW_BDW,B1.YJBFZLSJ,B1.YJBFZL,B2.GBTJ,B2.IS_CFJC, B2.IS_ZZCS,B2.IS_MFHC,B2.IS_ZBHC,B2.IS_TSGJ,B2.IS_YXZLPH,B2.YWJNR,B2.BJ,B2.WJGXJX,B2.XGWJZXZTDC,B2.GCPGJL,B2.JLFJID, B3.JSWJLX,B3.JSWJBH,B3.JSWJZT,B3.BB AS A_BB,B3.ID AIRWORTHINESS_ID,B3.GLJSWJID,B3.SXRQ, B4.USERNAME || ' ' || B4.REALNAME AS PGR_ID, B10.USERNAME || ' ' || B10.REALNAME AS GBR_ID, CEIL(B1.PGQX - SYSDATE) AS SYTS, D011.ATTACHCOUNT, B5.DPRTNAME AS JG_DPRTNAME, B6.ZJH || ' ' ||B6.YWMS AS ZJH_ZJH, B7.PGDH AS GLPGDH,B7.BB AS GLPGDBB, B8.WBWJM JSWJ_WBWJM,B8.ID JSWJ_ID,B8.HZM JSWJ_HZM, B9.JSWJBH AS GLJSWJBH,B9.BB AS GLJSWJBB,B9.id AS GLJSWJIDS, B13.WBWJM SCFJ_WBWJM,B13.ID SID,B13.HZM SCFJ_HZM FROM B_G2_001 B1 LEFT JOIN B_G2_00100 B2 ON B1.ID = B2.MAINID LEFT JOIN B_G2_000 B3 ON B1.JSWJID = B3.ID LEFT JOIN T_USER B4 ON B1.PGRID=B4.ID LEFT JOIN (SELECT MAINID,COUNT(1) AS ATTACHCOUNT FROM D_011 WHERE YXZT=1 GROUP BY MAINID) D011 ON D011.MAINID = B1.ID LEFT JOIN T_DEPARTMENT B5 ON B1.DPRTCODE=B5.ID LEFT JOIN D_005 B6 ON B1.ZJH =B6.ZJH AND B1.DPRTCODE=B6.DPRTCODE LEFT JOIN B_G2_001 B7 ON B1.GLPGDID= B7.ID LEFT JOIN D_011 B8 ON B1.JSWJID=B8.MAINID LEFT JOIN B_G2_000 B9 ON B9.ID=B7.JSWJID LEFT JOIN T_USER B10 ON B1.GBRID= B10.ID LEFT JOIN D_011 B13 ON B2.JLFJID=B13.MAINID AND B13.YXZT=1
ORDER BY SYTS,b1.zt,b1.ID
ORDER BY ${pagination.sort} ${pagination.order}

