    WHERE PGDH     = #{ pgdh,     jdbcType = VARCHAR }
  	AND   DPRTCODE = #{ dprtcode, jdbcType = VARCHAR }

SELECT B1.PGDH,B1.BB,B1.PGSJ,B1.PGDZT,B1.ID,B1.JSWJID, B4.USERNAME || ' ' || B4.REALNAME AS PGR_ID, B2.ID AS AIRWORTHINESS_ID, B2.BB AS A_BB,B2.JSWJBH,B2.JSWJZT,B2.jswjlx,B2.bfrq,B2.sxrq,B2.dqrq FROM B_G2_001 B1 LEFT JOIN B_G2_000 B2 ON B1.JSWJID=B2.ID LEFT JOIN T_USER B4 ON B1.PGRID=B4.ID WHERE 1=1 AND B1.ZT IN (4,10)
AND B1.JSWJID = #{jswjid,jdbcType=VARCHAR}
AND B1.jx = #{jx,jdbcType=VARCHAR}
AND B1.dprtcode = #{dprtcode,jdbcType=VARCHAR}
order by b1.pgdh

SELECT B1.PGDH,B1.BB,B1.PGSJ,B1.PGDZT,B1.ID,B1.JSWJID, B4.USERNAME || ' ' || B4.REALNAME AS PGR_ID, B2.ID AS AIRWORTHINESS_ID, B2.BB AS A_BB,B2.JSWJBH,B2.JSWJZT,B2.jswjlx,B2.bfrq,B2.sxrq,B2.dqrq FROM B_G2_001 B1 LEFT JOIN B_G2_000 B2 ON B1.JSWJID=B2.ID LEFT JOIN T_USER B4 ON B1.PGRID=B4.ID WHERE 1=1
AND B1.JSWJID = #{jswjid,jdbcType=VARCHAR}
AND B1.jx = #{jx,jdbcType=VARCHAR}
order by b1.pgdh

