mapper.select:
SELECT Z009.ID,--审核项目单ID Z009.ZT,--状态：1保存、2提交、9关闭 Z009.SHXMDBH,--审核项目单编号 Z009.LX,--类型：1内部、2外部 Z009.SHDXID,--审核对象ID，关联部门ID Z009.SHDXBH,--审核对象编号 Z009.SHDXMC,--审核对象名称 Z009.SHLB,--审核类别：11初审、12复审、21转向审核 Z009.SJ_SHRQ,--实际审核日期YYYYMMDD Z901.SHCY, --审核成员 TUSER.REALNAME whr_realname,--维护人 TUSER.USERNAME whr_username,--维护人 Z009.WHSJ, --维护时间 jg.dprtname jg_dprtname, FJ.ATTACHCOUNT fjcount FROM B_Z_009 Z009 LEFT JOIN (SELECT Z901.YWID, WM_CONCAT(Z901.JS || '#_#' || Z901.CYID || '#_#' || Z901.CYBH || '#_#' || Z901.CYMC) SHCY FROM B_Z_901 Z901 WHERE Z901.DPRTCODE = #{dprtcode,jdbcType=VARCHAR} AND Z901.ZT = 1 AND Z901.YWLX = 13 GROUP BY YWID) Z901 ON Z901.YWID = Z009.ID LEFT JOIN T_USER TUSER ON TUSER.ID = Z009.WHRID left join t_department jg ON(Z009.dprtcode=jg.id)
LEFT JOIN (SELECT MAINID,COUNT(1) AS ATTACHCOUNT FROM D_011 where YXZT = 1 GROUP BY MAINID) FJ ON FJ.MAINID = Z009.id WHERE Z009.DPRTCODE = #{dprtcode,jdbcType=VARCHAR}
ORDER BY Z009.SJ_SHRQ DESC
order by ${pagination.sort} ${pagination.order}

		select count(1)
			from B_Z_009 b
		where b.dprtcode = #{dprtcode,jdbcType=VARCHAR}
		  
            and  b.id = #{id}
         
         
            and  b.shxmdbh = #{shxmdbh}
