mapper.select:
SELECT B.* ,CZR.USERNAME ||' '|| CZR.REALNAME CZR_REALNAME , B.DXID||'/'||B.PBRQ REC_HD FROM (SELECT B1.ID, B1.DXID, B2.DPRTCODE, to_char(B2.PBRQ, 'yyyy-mm-dd') PBRQ, B1.REC_CZLS, B1.REC_IP, B1.REC_CZRID, B1.REC_CZSM, B1.REC_CZSJ, ROW_NUMBER() OVER (PARTITION BY B1.REC_CZLS ORDER BY B1.REC_CZSJ DESC) RN FROM B_S_01001_REC B1 INNER JOIN B_S_010_REC B2 ON B1.MAINID = B2.ID AND B1.REC_CZLS = B2.REC_CZLS WHERE B1.DXLX = 10) B LEFT JOIN T_USER CZR ON CZR.ID = B.REC_CZRID WHERE B.RN = 1
and B.REC_CZLS = #{paramsMap.REC_CZLS,jdbcType=VARCHAR}
and B.DXID = #{paramsMap.ID,jdbcType=VARCHAR}
and B.DPRTCODE = #{paramsMap.DPRTCODE,jdbcType=VARCHAR}
and ( UPPER(B.DXID) like UPPER('%'||#{paramsMap.KEYWORD,jdbcType=VARCHAR}||'%') )
AND exists ( SELECT 1 from D_007 D where B.DXID = D.FJZCH AND B.DPRTCODE = D.DPRTCODE AND D.zt = 1 )
AND exists ( SELECT 1 from V_ROLE_JX D where B.DXID = D.FJZCH AND B.DPRTCODE = D.DPRTCODE AND D.USER_ID = #{paramsMap.LOGINED_USERID,jdbcType=VARCHAR} )
order by B.REC_CZSJ DESC
