mapper.sql:
     where 1 = 1
    
       
          and USERNAME = #{username}
       
       
        
          and PASSWORD = #{password}
       
       
          and ORGID = #{orgid}
       
       
        
         AND ORGID IN (
		   SELECT ID FROM CHILDREN_ORG
		 )

mapper.select:
  		WITH RECURSIVE CHILDREN_ORG AS
		(
			SELECT A.ID FROM T_AUTH_ORGANIZATION A WHERE A.ID = #{orgid,jdbcType=VARCHAR}
				UNION ALL 
			SELECT Z.ID FROM T_AUTH_ORGANIZATION Z INNER JOIN CHILDREN F ON F.ID = Z.PID
		)
	   SELECT COUNT(1) TOTAL 
	   FROM T_AUTH_USER U 
	   WHERE U.USERNAME= #{username,jdbcType=VARCHAR}
	   AND U.PASSWORD= #{password,jdbcType=VARCHAR}
	   AND U.ORGID IN (
		   SELECT ID FROM CHILDREN_ORG
		   )

mapper.update:
update T_AUTH_USER
set USERNAME = #{username,jdbcType=VARCHAR},
  PASSWORD = #{password,jdbcType=VARCHAR},
  ORGID = #{orgid,jdbcType=VARCHAR}
where ID = #{id,jdbcType=VARCHAR}
