mapper.select:
select B00402.ID, B00402.MAINID, B00402.FKRQ, B00402.FKFS, B00402.FKFSSM, B00402.FKJE, B00402.BZ, B00402.ZT, B00402.WHDWID, B00402.WHRID, B00402.WHSJ, UZDR.ID AS UZDRID,UZDR.USERNAME AS ZDRUSERNAME,UZDR.REALNAME AS ZDRREALNAME from B_H_00402 B00402 left join T_USER UZDR on B00402.WHRID = UZDR.ID where B00402.ZT = 1
order by B00402.FKRQ DESC
order by ${pagination.sort} ${pagination.order}

SELECT id as "ID",hth as "HTH",jjcd as "JJCD",dprtcode as "DPRTCODE",htqdrq as "HTQDRQ",htlx as "HTLX", gysbm as "GYSBM",gysmc as "GYSMC",yf as "YF",wf as "WF",yfbl as "YFBL",fkcs as "FKCS",dhbl as "DHBL" FROM ( select b1.id, b1.hth, b1.jjcd, b1.dprtcode, b1.htqdrq, b1.htlx, b1.gysbm, b1.gysmc, round(((CASE WHEN b1.fjfy IS NULL THEN 0 ELSE b1.fjfy END) + (CASE WHEN b3.fy IS NULL THEN 0 ELSE b3.fy END)), 2) as YF, round(((CASE WHEN b1.fjfy IS NULL THEN 0 ELSE b1.fjfy END) + (CASE WHEN b3.fy IS NULL THEN 0 ELSE b3.fy END) - (CASE WHEN b2.fkje IS NULL THEN 0 ELSE b2.fkje END)), 2) as WF, case when ((CASE WHEN b1.fjfy IS NULL THEN 0 ELSE b1.fjfy END) + (CASE WHEN b3.fy IS NULL THEN 0 ELSE b3.fy END)) = 0 then 0 else round((CASE WHEN b2.fkje IS NULL THEN 0 ELSE b2.fkje END) / ((CASE WHEN b1.fjfy IS NULL THEN 0 ELSE b1.fjfy END) + (CASE WHEN b3.fy IS NULL THEN 0 ELSE b3.fy END)) * 100, 2) end as YFBL, b2.fkcs as FKCS, case when (CASE WHEN b3.htsl IS NULL THEN 0 ELSE b3.htsl END) = 0 then 0 else round((CASE WHEN b3.dhsl IS NULL THEN 0 ELSE b3.dhsl END) / b3.htsl * 100, 2) end as DHBL from b_h_004 b1 join (select mainid, count(1) fkcs, sum(fkje) fkje from b_h_00402 where zt = 1 group by mainid) b2 on b2.mainid = b1.id and b1.zt in (2,10) join (select mainid, sum(((CASE WHEN ht_clf IS NULL THEN 0 ELSE ht_clf END) + (CASE WHEN ht_gsf IS NULL THEN 0 ELSE ht_gsf END) + (CASE WHEN ht_qtf IS NULL THEN 0 ELSE ht_qtf END)) * (CASE WHEN ht_sl IS NULL THEN 0 ELSE ht_sl END)) fy, sum(ht_sl) htsl, sum(dhsl) dhsl from b_h_00401 where zt = 1 group by mainid) b3 on b1.id = b3.mainid where 1 = 1
) A
order by "HTQDRQ" desc, "HTLX" asc, "HTH" asc
order by "${pagination.sort}" ${pagination.order}

SELECT id as "ID",hth as "HTH",jjcd as "JJCD",dprtcode as "DPRTCODE",htqdrq as "HTQDRQ", htlx as "HTLX",gysbm as "GYSBM",gysmc as "GYSMC",fkrq as "FKRQ",fkfs as "FKFS", fkje as "FKJE",bz as "BZ" FROM ( select b2.id, b2.hth, b2.jjcd, b2.dprtcode, b2.htqdrq, b2.htlx, b2.gysbm, b2.gysmc, b1.fkrq, case fkfs when 0 then b1.fkfssm when 1 then '支票' when 2 then '现金' when 3 then '汇款' else '-' end as FKFS, b1.fkje, b1.bz from b_h_00402 b1 join b_h_004 b2 on b1.mainid = b2.id and b2.zt in (2,10) and b1.zt = 1 where 1 = 1
) A
order by "HTQDRQ" desc, "HTLX" asc, "HTH" asc, "FKRQ" asc
order by "${pagination.sort}" ${pagination.order}
