select
,u.realname,u.username,u.id WHR_ID, (select t_user.username || ' ' || t_user.realname from t_user where t_user.id = a.sc_blrid) SC_BLRXX,
(select t_user.username || ' ' || t_user.realname from t_user where t_user.id = a.sc_pzrid) SC_PZRXX, (select t_user.username || ' ' || t_user.realname from t_user where t_user.id = a.zc_blrid) ZC_BLRXX, (select t_user.username || ' ' || t_user.realname from t_user where t_user.id = a.zc_pzrid) ZC_PZRXX from B_S_013 a left join d_005 b on a.zjh = b.zjh AND A.DPRTCODE = B.DPRTCODE left join t_user u on u.id = a.WHRID where a.ID = #{id,jdbcType=VARCHAR}

select count(1) total from ( select * from ( select b1.id, b1.gzbldh, b1.fjzch, b1.fxjldh, b1.jlbym,
b1.is_m, b1.mel, b1.zjh, b1.sc_blrid, (select t_user.username || ' ' || t_user.realname from t_user where t_user.id = b1.sc_blrid) sc_blrxx, b1.sc_zzh, b1.sc_sqrq, b1.sc_dqrq, b1.sc_pzrid, (select t_user.username || ' ' || t_user.realname from t_user where t_user.id = b1.sc_pzrid) sc_pzrxx, b1.zc_blrid, (select t_user.username || ' ' || t_user.realname from t_user where t_user.id = b1.zc_blrid) zc_blrxx, b1.zc_zzh, b1.zc_sqrq, b1.zc_dqrq, b1.zc_pzrid, (select t_user.username || ' ' || t_user.realname from t_user where t_user.id = b1.zc_pzrid) zc_pzrxx,
b1.gzms, b1.bz, b1.zt, b1.whdwid, b1.whrid, (select t_user.username || ' ' || t_user.realname from t_user where t_user.id = b1.whrid) whrxx, whr.realname,whr.username,whr.id WHR_ID,
b1.whsj, b1.dprtcode, (select dprtname from t_department where id = b1.dprtcode ) dprtname, b1.gbrid, (select t_user.username || ' ' || t_user.realname from t_user where t_user.id = b1.gbrid) gbrxx, b1.gbrq, b1.gbsm, b2.gdxx from b_s_013 b1 left join t_user whr on whr.id = b1.WHRID left join (select c.mainid, string_agg(
c.gdlx || ' ' || c.gdzlx || ' ' || c.gdbh || ' ' || c.zt ,',' ) gdxx from (select a1.mainid, a1.gdlx, a2.gdlx gdzlx, a2.gdbh, a2.zt from b_s_01301 a1 join b_g_007 a2 on a1.xggdid = a2.id and a1.gdlx = 2 and a1.zt = 1 union all select a1.mainid, a1.gdlx, 0 gdzlx, a2.gdbh, a2.zt from b_s_01301 a1 join b_g_010 a2 on a1.xggdid = a2.id and a1.gdlx = 3 and a1.zt = 1) c group by mainid) b2 on b2.mainid = b1.id
where b1.zt in (1, 2, 9, 10)
and ( b1.gzbldh like '%'||#{keyword}||'%' or (b1.fjzch like '%'||#{keyword}||'%') or (b1.fxjldh like '%'||#{keyword}||'%') or (b1.jlbym like '%'||#{keyword}||'%') or (b1.gzms like '%'||#{keyword}||'%' )
