and b1.id = #{id,jdbcType=VARCHAR}
and b1.dprtcode = #{dprtcode,jdbcType=VARCHAR}
and b1.gzbldh = #{gzbldh,jdbcType=VARCHAR}
AND b1.id != #{paramsMap.idneq,jdbcType=VARCHAR}
