mapper.select:
SELECT * FROM (
select BW00101.ID as "ID", BW00101.MAINID as "MAINID", BW00101.BJXLH as "BJXLH", BW00101.ZWMS as "ZWMS", BW00101.YWMS as "YWMS", BW00101.GG as "GG", BW00101.XH as "XH", BW00101.BZ as "BZ", BW00101.JY_SCRQ as "JY_SCRQ", BW00101.JY_XCRQ as "JY_XCRQ", BW00101.JY_ZQ as "JY_ZQ", BW00101.WHBMID as "WHBMID", BW00101.WHRID as "WHRID", BW00101.WHSJ as "WHSJ", to_date(to_char(BW00101.JY_XCRQ, 'YYYY-MM-DD'), 'YYYY-MM-DD') - to_date(to_char(current_timestamp, 'YYYY-MM-DD'), 'YYYY-MM-DD') as "SYTS", BW001.BJXLH AS "MBJXLH", BW001.DPRTCODE as "DPRTCODE", D008.BJH as "BJH", D008.ZWMS as "HCZWMS", D008.YWMS as "HCYWMS", D008.HCLX as "HCLX", U.ID as "WID", U.USERNAME as "USERNAME", U.REALNAME as "REALNAME" from B_W_00101 BW00101 join (SELECT BJXLH, MAINID, MAX(JY_SCRQ) AS JY_SCRQ FROM B_W_00101 where ZT = 1 GROUP BY BJXLH, MAINID) A on BW00101.BJXLH = A.BJXLH and BW00101.MAINID = A.MAINID and BW00101.JY_SCRQ = A.JY_SCRQ left join B_W_001 BW001 on BW00101.MAINID = BW001.ID left join D_008 D008 on D008.ID = BW001.BJID left join T_USER U on BW00101.WHRID = U.ID where BW00101.ZT = 1 AND EXISTS(SELECT 1 FROM B_H_001 B WHERE BW001.BJID = B.BJID and BW001.BJXLH = B.SN)
) A
order by "SYTS" ASC,"JY_SCRQ" ASC
order by "${pagination.sort}" ${pagination.order}

mapper.update:
update B_W_00101 set ZT = 0
where BJXLH = #{bjxlh,jdbcType=VARCHAR} and MAINID = #{mainid,jdbcType=VARCHAR}
	and (
		0 = #{bjbs} 
		or
		(	1 = #{bjbs} 
			and 
			not exists (select 1 from B_W_00101 where ZT = 1 and BJBS = 0 and BJXLH = #{bjxlh,jdbcType=VARCHAR} and MAINID = #{mainid,jdbcType=VARCHAR}) 
		)
		)
