mapper.select:
SELECT B.ID AS "ID", B.BJH AS "BJH", B.MS AS "MS", B.TDJH AS "TDJH", B.TDJMS AS "TDJMS", B.JXBS AS "JXBS", B.GKBS AS "GKBS", B.KNXBS AS "KNXBS", B.ZT AS "ZT", B.DPRTCODE AS "DPRTCODE", B.WHBMID AS "WHBMID", B.WHRID AS "WHRID", B.WHSJ AS "WHSJ", B.REC_XGLX AS "REC_XGLX", B.REC_CZRID AS "REC_CZRID", B.REC_CZSJ AS "REC_CZSJ", B.REC_IP AS "REC_IP", B.REC_CZSM AS "REC_CZSM", B.REC_CZLS AS "REC_CZLS", B.REC_ZBID AS "REC_ZBID" ,WHR.USERNAME||' '||WHR.REALNAME AS "REC_CZR_NAME" , ( F_GETREC_HD(B.REC_CZSM) ) AS "REC_HD" FROM D_017_REC B LEFT JOIN T_USER WHR ON WHR.ID = B.REC_CZRID WHERE 1=1
and B.REC_CZLS = #{paramsMap.REC_CZLS,jdbcType=VARCHAR}
and B.ID = #{paramsMap.ID,jdbcType=VARCHAR}
and B.DPRTCODE = #{paramsMap.DPRTCODE,jdbcType=VARCHAR}
and (B.BJH like '%'||#{paramsMap.KEYWORD,jdbcType=VARCHAR}||'%')
order by B.REC_CZSJ DESC
