mapper.select:
select t1.ID, t1.DPRTCODE, t1.CSID,t1.ZJ, t1.GYSMC, t1.LXR, t1.ZW, t1.SJ, t1.ZJ, t1.CZ, t1.YXDZ, t1.QQ, t1.WX, t1.BZ, t1.ZT, t1.WHRID, t1.WHSJ, (whr.username || ' ' || whr.realname) username,dprt.DPRTNAME from d_016 t1 left join d_015 t2 on t1.csid=t2.id LEFT JOIN t_user whr on t1.whrid=whr.id and whr.jgdm=t1.dprtcode left join T_DEPARTMENT dprt on t1.dprtcode=dprt.id
order by t1.whsj desc
order by ${pagination.sort} ${pagination.order}

mapper.sql:
	   where 1=1 and t1.zt=1
         
             and t1.dprtcode =  #{dprtcode,jdbcType=VARCHAR}
         
         
		      and (t1.zw like '%'||#{keyword,jdbcType=VARCHAR}||'%'
		       or  t1.lxr like '%'||#{keyword}||'%'
		     )              
		  
	        
            and  t1.id  = #{id,jdbcType=VARCHAR}

mapper.select:
select 

from D_016
where ZT = 1 and CSID = #{csid,jdbcType=VARCHAR}

SELECT 

FROM D_016
WHERE ZT = 1 AND DPRTCODE = #{dprtcode,jdbcType=VARCHAR}

update.foreach:
UPDATE D_016
CSID = #{item.csid,jdbcType=VARCHAR}, GYSMC = #{item.gysmc,jdbcType=VARCHAR}, LXR = #{item.lxr,jdbcType=VARCHAR}, ZW = #{item.zw,jdbcType=VARCHAR}, SJ = #{item.sj,jdbcType=VARCHAR}, ZJ = #{item.zj,jdbcType=VARCHAR}, CZ = #{item.cz,jdbcType=VARCHAR}, YXDZ = #{item.yxdz,jdbcType=VARCHAR}, QQ = #{item.qq,jdbcType=VARCHAR}, WX = #{item.wx,jdbcType=VARCHAR}, BZ = #{item.bz,jdbcType=VARCHAR}, ZT = #{item.zt,jdbcType=DECIMAL}, WHRID = #{item.whrid,jdbcType=VARCHAR}, WHSJ = current_timestamp
where ID = #{item.id,jdbcType=VARCHAR};
