mapper.insert:
insert into B_J_005 (ID, YPGG, YPMD, 
  ms, DPRTCODE, ZT, 
  WHBMID, WHRID, WHSJ
  )
values (#{id,jdbcType=VARCHAR}, #{ypgg,jdbcType=VARCHAR}, #{ypmd,jdbcType=DECIMAL}, 
  #{ms,jdbcType=VARCHAR}, #{dprtcode,jdbcType=VARCHAR}, #{zt,jdbcType=DECIMAL}, 
  #{whbmid,jdbcType=VARCHAR}, #{whrid,jdbcType=VARCHAR}, #{whsj,jdbcType=TIMESTAMP}
  )

mapper.select:
select b_j_005.ID, b_j_005.YPGG, b_j_005.YPMD, b_j_005.ms, b_j_005.DPRTCODE, b_j_005.ZT, b_j_005.WHBMID, b_j_005.WHRID, b_j_005.WHSJ, whr.realname whr_realname,whr.username whr_username, bm.dprtname bm_dprtname, jg.dprtname jg_dprtname from b_j_005 left join t_user whr on b_j_005.whrid=whr.id left join t_department bm on(b_j_005.whbmid=bm.id) left join t_department jg on(b_j_005.dprtcode=jg.id)
order by whsj desc
order by ${pagination.sort} ${pagination.order}

mapper.sql:
where 1=1 and b_j_005.zt =1
and b_j_005.id = #{id}
and b_j_005.dprtcode = #{dprtcode}
and ( UPPER(b_j_005.ypgg) like UPPER('%'||#{keyword}||'%') or UPPER(b_j_005.ypmd) like UPPER('%'||#{keyword}||'%') or UPPER(whr.USERNAME) like UPPER('%'||#{keyword}||'%') or UPPER(whr.REALNAME) like UPPER('%'||#{keyword}||'%') OR UPPER(b_j_005.ms) like UPPER('%'||#{keyword}||'%'))

mapper.select:
		select 
             count(1)
              from b_j_005 
	 			where 1=1 and b_j_005.zt =1


		         and  b_j_005.ypgg = #{ypgg}



