mapper.select:
	select id,realname,username,bmdm from t_user where username != 'admin' and STATE = 1


           and JGDM = #{jgdm}


           and id = #{id}


           and username = #{username}

	select ID, USERNAME, REALNAME, CELLPHONE, PHONE, SEX, STATE, 
  BMDM,JGDM,email from t_user where 1=1


           and JGDM = #{jgdm}


           and username = #{username}

  	select t_user.id,t_user.realname,t_user.username,t_user.bmdm,t_user.sex,t_user.email,Department.dprtname dprtname,Department.dprtcode dprtbh  from t_user left join t_department Department on (t_user.bmdm=Department.id) where username != 'admin' and STATE = 1


             and t_user.JGDM = #{jgdm}


             and t_user.id = #{id}


		    and (  UPPER(USERNAME) like UPPER('%'||#{keyword}||'%')
		    	or UPPER(REALNAME) like UPPER('%'||#{keyword}||'%') )

mapper.update:
	 update T_USER
  set PASSWORD = #{password,jdbcType=VARCHAR}
  where ID = #{id,jdbcType=VARCHAR}

	 update T_USER
  set STATE = #{state,jdbcType=DECIMAL}
  where ID = #{id,jdbcType=VARCHAR}

mapper.select:
select t.ID, t.USERNAME,t.PASSWORD, t.REALNAME, t.CELLPHONE, t.PHONE, t.SEX,t.email, t.STATE, t.LASTVISIT, t.LASTIP, t.DRZHID, t.BMDM,t.JGDM , dprt_bm.dprtname bm_deptname, dprt_jg.dprtname jg_dprtname,bb.username accountName from t_user t left join t_department dprt_bm on (t.bmdm = dprt_bm.id) left join t_department dprt_jg on (t.jgdm = dprt_jg.id) left join t_login bb on t.drzhid=bb.id
order by t.USERNAME asc
order by ${pagination.sort} ${pagination.order}

	select id,xm,lx,departmentname,jgdm from (

	select t1.id, (CASE WHEN t2.id IS null THEN t1.xm ELSE t2.username||' '||t2.realname END) xm, '1' lx,t3.dprtname departmentName ,t1.dprtcode jgdm from b_z_001 t1 
    left join t_user t2 on t1.wxryid = t2.id left join t_department t3 on t1.wxrbmid=t3.id
    where t1.zt = 1  and t1.dprtcode=#{jgdm}

    union all 

    select tt1.id, tt1.username||' '||tt1.realname xm, '2' lx,tt3.dprtname departmentName,tt1.jgdm from t_user tt1 left join t_department tt3 on tt1.bmdm=tt3.id
    where tt1.state =1 and not exists(select 1 from b_z_001 tt2 where tt2.zt =1 and tt2.wxryid = tt1.id)  and tt1.jgdm=#{jgdm}
  	) AS A



   		order by lx asc


	    order by ${pagination.sort} ${pagination.order}

  		select count(*) FROM T_USER U
	     where 1=1


             and ID = #{id}


             and USERNAME = #{username}


             and PASSWORD= #{password}

