mapper.select:
  	select t_user.id,realname,username,sex,email,bmdm,t_department.dprtname as dprtname,t_department.dprtcode dprtbh from t_user 
	left join t_department on  t_department.id=t_user.bmdm where STATE = 1 and JGDM = #{dprtcode,jdbcType=VARCHAR}

        and t_user.bmdm = #{bmdm,jdbcType=VARCHAR}

	  AND (UPPER(realname) LIKE UPPER('%'||#{str,jdbcType=VARCHAR}||'%')
		     OR   UPPER(username) LIKE UPPER('%'||#{str,jdbcType=VARCHAR}||'%')

		     	)
