mapper.select:
select
from B_H_002 B left join T_USER U on B.SQRID = U.ID left join T_USER U2 on B.SPRID = U2.ID left join T_USER U3 on B.ZDJSRID = U3.ID left join (select DJID,count(1) as NOREAD from B_H_005 T where EXISTS (select 1 from B_H_00501 T1 where T1.MAINID = T.ID and T1.ZT=0 and T1.JSRID = #{zdrid}) group by T.DJID) B1 on B1.DJID = B.ID left join (select DJID,count(1) as READALL from B_H_005 group by DJID) B2 on B2.DJID = B.ID left join (select B.MAINID,WM_CONCAT(GDID||'#'||GDBH||'#'||GDLX) AS GLGD from B_H_00202 B GROUP BY MAINID) B00202 on B.ID = B00202.MAINID where B.ZT NOT IN (8)
AND exists ( select 1 from B_H_00201 BH00201 where BH00201.ZT=1 and BH00201.MAINID=b.id
AND BH00201.BJH = #{paramsMap.bjh, jdbcType=VARCHAR}
)
order by B.SQSJ ${pagination.order}
order by ${pagination.sort} ${pagination.order}

select
from B_H_002 B left join T_USER U on B.SQRID = U.ID left join T_USER U2 on B.SPRID = U2.ID left join T_USER U3 on B.ZDJSRID = U3.ID left join (select DJID,count(1) as NOREAD from B_H_005 T where EXISTS (select 1 from B_H_00501 T1 where T1.MAINID = T.ID and T1.ZT=0 and T1.JSRID = #{zdrid}) group by T.DJID) B1 on B1.DJID = B.ID left join (select DJID,count(1) as READALL from B_H_005 group by DJID) B2 on B2.DJID = B.ID left join (select B.MAINID,WM_CONCAT(GDID||'#'||GDBH||'#'||GDLX) AS GLGD from B_H_00202 B GROUP BY MAINID) B00202 on B.ID = B00202.MAINID where B.ZT NOT IN (1,8)
AND exists ( select 1 from B_H_00201 BH00201 where BH00201.ZT=1 and BH00201.MAINID=b.id
AND BH00201.BJH = #{paramsMap.bjh, jdbcType=VARCHAR}
)
order by B.SQSJ ${pagination.order}
order by ${pagination.sort} ${pagination.order}
