mapper.sql:
where 1 = 1

    and  lower(t.XMBM) like lower("%",#{xmbm},"%")


    and  lower(t.XMMC) like lower("%",#{xmmc},"%")


    and  lower(t.FJZCH) like lower("%",#{FJZCH},"%")


    and  lower(t.FJXLH) like lower("%",#{FJXLH},"%")


    and  t.ZT = #{zt}


    and  t.XMZL = #{xmzl}


      and  t.DPRTCODE = #{dprtcode}

mapper.select:
SELECT A.* FROM (SELECT ,
t2.DPRTNAME AS dprtname, t3.username||' '||t3.realname AS whr, D011.ATTACHCOUNT, (CASE WHEN t.WBBS = 0 Then (SELECT t5.dprtcode FROM t_department t5 WHERE t.khid = t5.id) WHEN t.WBBS = 1 Then (SELECT t4.khbm FROM D_019 t4 WHERE t.khid = t4.id) END) khbm, (CASE WHEN t.WBBS = 0 Then (SELECT t5.DPRTNAME FROM t_department t5 WHERE t.khid = t5.id) WHEN t.WBBS = 1 Then (SELECT t4.khmc FROM D_019 t4 WHERE t.khid = t4.id) END) khmc FROM D_020 t LEFT JOIN t_department t2 on t.dprtcode = t2.id LEFT JOIN t_user t3 on t.whrid = t3.id LEFT JOIN (SELECT MAINID,COUNT(1) AS ATTACHCOUNT FROM D_011 where YXZT = 1 GROUP BY MAINID) D011 ON D011.MAINID = t.ID
) A WHERE 1 = 1
and ( (lower(A.khbm) like lower('%'||#{keyword}||'%')) or (lower(A.khmc) like lower('%'||#{keyword}||'%')) or (lower(A.XMBM) like lower('%'||#{keyword}||'%')) or (lower(A.XMMC) like lower('%'||#{keyword}||'%')) or (lower(A.FJZCH) like lower('%'||#{keyword}||'%')) )
order by A.WHSJ DESC
order by ${pagination.sort} ${pagination.order}

SELECT 
FROM D_020 t
WHERE ID = #{id, jdbcType=VARCHAR}

SELECT 
FROM D_020 t
WHERE  DPRTCODE = #{dprtcode,jdbcType=VARCHAR} AND XMBM = #{xmbm,jdbcType=VARCHAR}

SELECT (CASE WHEN t.WBBS = 0 Then (SELECT t5.DPRTNAME FROM t_department t5 WHERE t.khid = t5.id) WHEN t.WBBS = 1 Then (SELECT t4.khmc FROM D_019 t4 WHERE t.khid = t4.id) END) khmc, (CASE WHEN t.WBBS = 0 Then (SELECT t5.dprtcode FROM t_department t5 WHERE t.khid = t5.id) WHEN t.WBBS = 1 Then (SELECT t4.khbm FROM D_019 t4 WHERE t.khid = t4.id) END) khbm,
FROM D_020 t WHERE t.dprtcode = #{dprtcode, jdbcType=VARCHAR} and zt in (2,10)
and t.fjjx= #{fjjx, jdbcType=VARCHAR}
and t.fjjx is null
and t.wbbs = #{wbbs,jdbcType=INTEGER}
and t.khid= #{khid, jdbcType=VARCHAR}
and t.fjzch= #{fjzch, jdbcType=VARCHAR}
and t.fjxlh= #{fjxlh, jdbcType=VARCHAR}
and ( (lower(t.XMBM) like lower('%'||#{keyword}||'%')) or (lower(t.XMMC) like lower('%'||#{keyword}||'%')) )
