mapper.sql:
where 1=1 and b.jd = #{jd}

	and b.pbrq >= #{scheduleDateBegin}


	and b.pbrq <= #{scheduleDateEnd}



	and b.dprtcode = #{dprtcode}

mapper.select:
SELECT D1.FJZCH, B.ID PBID, B.PBRQ FROM D_007 D1 LEFT JOIN (select b1.dxid, b2.dprtcode, b2.id, b2.pbrq from B_S_01001 b1 join B_S_010 B2 ON B1.MAINID = B2.ID AND B2.ZT in (1, 2, 3) AND B2.PBLX = 1 AND B1.DXLX = 10 AND B1.ZT = 1 AND B2.PBRQ BETWEEN TO_DATE(#{paramsMap.pbrqBegin,jdbcType=VARCHAR},'YYYY-MM-DD') AND TO_DATE(#{paramsMap.pbrqEnd,jdbcType=VARCHAR},'YYYY-MM-DD')) b on b.dxid = d1.fjzch and b.dprtcode = d1.dprtcode WHERE D1.ZT = 1
AND D1.JD = #{jd,jdbcType=VARCHAR}
AND D1.DPRTCODE = #{dprtcode,jdbcType=VARCHAR} AND EXISTS (SELECT 1 FROM V_ROLE_JX A WHERE A.DPRTCODE = D1.DPRTCODE AND A.FJZCH = D1.FJZCH AND A.USER_ID = #{paramsMap.userId, jdbcType=VARCHAR}) ORDER BY D1.FJZCH, B.PBRQ

