mapper.sql:
	where
	1=1 


		and b.dprtcode = #{dprtcode}


		and b.zjh = #{zjh}


		and b.fjzch = #{fjzch}


		and ( UPPER(b.fjzch) like UPPER('%'||#{keyword}||'%')
		or UPPER(b.gzxx) like UPPER('%'||#{keyword}||'%')
		)


	    and b.ID = #{id}



    		 AND exists 
    		( SELECT 1 from D_007 where 
    		b.fjzch = FJZCH
    		AND b.dprtcode = DPRTCODE
    		AND zt = 1
    		)


    		 AND exists 
    		( SELECT 1 from V_ROLE_JX where 
    		b.fjzch= FJZCH
    		AND b.dprtcode = DPRTCODE
    		AND USER_ID = #{paramsMap.userId,jdbcType=VARCHAR}
    		)

mapper.select:
select B.ID, B.DPRTCODE, B.FJZCH, B.GZXX, B.BZ, B.GBZT, B.whdwid, B.whrid, B.whsj, B.GBRID, B.GBSJ, B.GBYY,B.ZJH,B1.zjh||' '||B1.ywms||' '||B1.ZWMS zjhms,D011.ATTACHCOUNT, t.username||' '||t.realname zdrname,D.GZCS,D.ZJFSRQ from b_s_019 b LEFT JOIN t_user t on b.whrid=t.id LEFT JOIN (SELECT MAINID,COUNT(1) AS ATTACHCOUNT FROM D_011 where YXZT = 1 GROUP BY MAINID) D011 ON D011.MAINID=b.id LEFT JOIN ( select count(0) AS gzcs,max(hbrq)AS zjfsrq,MAINID from b_s_01901 where zt=1 group by mainid) D ON D.MAINID = B.ID LEFT JOIN d_005 b1 on b.zjh=b1.zjh and b.dprtcode=b1.dprtcode
order by whsj desc
order by ${pagination.sort} ${pagination.order}
