mapper.select:
select count(1)
from B_S2_007
where ZT in(1,2,7) and DTBS = 1 and DPRTCODE = #{dprtcode,jdbcType=VARCHAR} and FJZCH = #{fjzch,jdbcType=VARCHAR}

  		select 

    	from b_s2_007 B
    	where 1=1

			and ( 
			   UPPER(B.gbbh) like UPPER('%'||#{paramsMap.keyword}||'%')
			OR UPPER(B.gbmc) like UPPER('%'||#{paramsMap.keyword}||'%')
			)


			and B.dprtcode = #{dprtcode}


			and B.fjzch = #{fjzch}


			and (B.fjzch = '-' or B.fjzch is null)


			and B.JH_ZXDWID = #{jhZxdwid}


			and B.zt = #{zt}


		    and B.ZT in

          			#{item}  





	    		AND exists 
	    		( SELECT 1 from D_007 where 
	    			B.FJZCH = FJZCH
	    		AND B.dprtcode = DPRTCODE
	    		AND zt = 1
	    		)


	    		AND (exists 
	    		( SELECT 1 from V_ROLE_JX where 
	    			B.FJZCH = FJZCH
	    		AND B.dprtcode = DPRTCODE
	    		AND USER_ID = #{paramsMap.userId,jdbcType=VARCHAR}
	    		)
	    		or B.FJZCH='-'
	    		or B.FJZCH is null
	    		)




				order by zt


				order by ${pagination.sort} ${pagination.order}

select b.id, b.dprtcode, b.whdwid, b.whrid, b.whsj, b.xfrdwid,b.zdrq, b.xfrid, b.xfsj, b.gbrid, b.gbrq, b.gbyy,b.zt, b.dtbs, b.wgbs, b.gbbh, b.gbmc, b.fjzch, b.gbxfdwid, b.jh_wwbs, b.jh_zxdwid, b.jh_zxdw, b.jh_ksrq, b.jh_jsrq, b.wxlx, b.gzyq, b.bz, b.sj_wwbs, b.sj_zxdwid, b.sj_zxdw, b.sj_ksrq, b.sj_jsrq, b.sj_gzz, b.sj_jcz, b.sj_zd, b.wgfkfjid ,t.USERNAME as whr_username,t.REALNAME as whr_realname,t1.dprtname as dprt_dprtname, d.xlh as a_xlh,d.fjjx as a_fjjx,t2.dprtname as xfdw_dprtname, (select count(1) from d_011 where D_011.mainid=b.id and D_011.yxzt=1) as ATTACHCOUNT from b_s2_007 b left join t_user t on b.whrid=t.id left join t_department t1 on b.dprtcode=t1.id left join d_007 d on b.fjzch=d.fjzch and b.dprtcode=d.dprtcode left join t_department t2 on b.gbxfdwid=t2.id
order by zt
order by ${pagination.sort} ${pagination.order}

mapper.sql:
		where 1=1 

			and ( UPPER(b.gbbh) like UPPER('%'||#{keyword}||'%')
			OR UPPER(b.gbmc) like UPPER('%'||#{keyword}||'%')
			OR UPPER(b.jh_zxdw) like UPPER('%'||#{keyword}||'%'))              


			and b.dprtcode = #{dprtcode}


			and b.fjzch = #{fjzch}


			and (b.fjzch is null or b.fjzch ='-')


			and b.zt = #{zt}


			and b.wxlx = #{wxlx}


			and b.DTBS = #{dtbs}


			and b.jh_zxdwid = #{jhZxdwid}


			and b.jh_wwbs = #{jhWwbs}


			and b.jh_wwbs = #{jhWwbs}


			and b.xfsj >= to_date(#{paramsMap.xfrqBegin, jdbcType=VARCHAR},'YYYY-MM-DD HH24:MI:SS')


			and b.xfsj <= to_date(#{paramsMap.xfrqEnd, jdbcType=VARCHAR},'YYYY-MM-DD HH24:MI:SS')


			and b.jh_jsrq >= to_date(#{paramsMap.xfrqBegin, jdbcType=VARCHAR},'YYYY-MM-DD HH24:MI:SS')


			and b.jh_jsrq <= to_date(#{paramsMap.xfrqEnd, jdbcType=VARCHAR},'YYYY-MM-DD HH24:MI:SS')


		    and b.ZT in

          			#{item}  



		    and b.id in

          			#{item}  




			and b.id = #{id}



		    and 
		    (   b.zt in

					#{gbzt}

		    )


		    and (b.fjzch  = #{paramsMap.fjzch} )


		    and (b.fjzch  is null or b.fjzch = '-' )


		    and (
		    	   UPPER(b.gbbh) like UPPER('%'||#{paramsMap.winkeyword}||'%')
		        )





	    		AND (exists 
	    		( SELECT 1 from D_007 where 
	    			B.FJZCH = FJZCH
	    		AND B.dprtcode = DPRTCODE
	    		AND zt = 1
	    		)
	    		or B.FJZCH is null
	    		or b.fjzch ='-'
	    		)


	    		AND (exists 
	    		( SELECT 1 from V_ROLE_JX where 
	    			B.FJZCH = FJZCH
	    		AND B.dprtcode = DPRTCODE
	    		AND USER_ID = #{paramsMap.userId,jdbcType=VARCHAR}
	    		)
	    		or B.FJZCH is null
	    		or b.fjzch ='-'
	    		)

mapper.update:
		update  b_s2_007
		set DTBS = #{dtbs,jdbcType=DECIMAL}
      	where id = #{id,jdbcType=VARCHAR}

		update  b_s2_007
		set DTBS = #{dtbs,jdbcType=DECIMAL},
			ZT = #{zt,jdbcType=DECIMAL}
      	where id = #{id,jdbcType=VARCHAR}

mapper.select:
SELECT B.ID, B.GBBH, B.GBMC, WO.ID AS WO_ID, WO.GDLX AS WO_GDLX, WO.GDBH AS WO_GDBH, WO.GDBT AS WO_GDBT, WO.GZXX AS WO_GZXX, WO.CLCS AS WO_CLCS, WO.SJ_JSRQ AS WO_SJ_JSRQ, WO.SJ_GZZ AS WO_SJ_GZZ, WO.SJ_JCZ AS WO_SJ_JCZ, WO.SJ_GS AS WO_SJ_GS, WO.SJ_ZD AS WO_SJ_ZD, WXXM.WXXMLX AS WO_WXXMLX,WO.SJ_JCZID AS WO_SJ_JCZID, WO.HSGS AS WO_HSGS, WORKER.ID AS WORKERID, WORKER.GZZ AS WORKERGZZ, WORKER.GZZID AS WORKERGZZID, ATT.ID AS ATTID, ATT.MAINID AS ATTMAINID, ATT.YSWJM AS ATTYSWJM, ATT.WBWJM AS ATTWBWJM, ATT.NBWJM AS ATTNBWJM, ATT.WJDX AS ATTWJDX, ATT.HZM AS ATTHZM, ATT.SM AS ATTSM, ATT.CFLJ AS ATTCFLJ, ATT.CZSJ AS ATTCZSJ, ATT.CZBMID AS ATTCZBMID, CASE WHEN ATTUSER.USERNAME IS NOT NULL THEN ATTUSER.USERNAME || ' ' ||ATTUSER.REALNAME ELSE NULL END AS ATTCZRNAME FROM B_S2_007 B INNER JOIN B_S2_008 WO ON B.ID = WO.GBID AND WO.GDLX != 9 AND WO.ZT IN (7, 10) AND WO.XMBLBS = 0
AND WO.ZT = #{paramsMap.zt,jdbcType=DECIMAL}
LEFT JOIN B_S2_00802 WORKER ON WO.ID = WORKER.MAINID AND WORKER.ZT = 1 LEFT JOIN B_G2_012 WXXM ON WO.LYRWID = WXXM.ID LEFT JOIN D_011 ATT ON ATT.MAINID = WO.ID AND ATT.YXZT = 1 LEFT JOIN T_USER ATTUSER ON ATT.CZRID = ATTUSER.ID WHERE B.FJZCH = #{fjzch,jdbcType=VARCHAR} AND B.DPRTCODE = #{dprtcode,jdbcType=VARCHAR}
AND ( UPPER(B.GBBH) LIKE UPPER('%'||#{paramsMap.gb}||'%') OR UPPER(B.GBMC) LIKE UPPER('%'||#{paramsMap.gb}||'%'))
AND ( UPPER(WO.GDBH) LIKE UPPER('%'||#{paramsMap.gd}||'%') OR UPPER(WO.GDBT) LIKE UPPER('%'||#{paramsMap.gd}||'%'))

	SELECT COUNT(1) FROM B_S2_007 
    WHERE gbbh     = #{ gbbh,     jdbcType = VARCHAR }
  	AND   DPRTCODE = #{ dprtcode, jdbcType = VARCHAR }
