mapper.select:
SELECT B.ID, B.FJZCH, B.BJLX, B.JH, B.XLH, B.NBSBM, (CASE WHEN D2.ZJH IS NULL THEN D4.ZJH ELSE D2.ZJH END) ZJH, (CASE WHEN D3.CJJH IS NULL THEN B.CJJH ELSE D3.CJJH END) CJJH, (CASE WHEN D3.ZWMS IS NULL THEN B.ZWMC ELSE D3.ZWMS END) ZWMC, (CASE WHEN D3.YWMS IS NULL THEN B.YWMC ELSE D3.YWMS END) YWMC, B.BJGZJL, B.ZJSL, B.WZ, B.ZT, B.SCRQ, B.BZ, B.AZRQ, B.AZJLDH, B.CCRQ, B.CCJLDH, B.LLKLX, B.LLKBH, B.KZLX, B.IS_DJ, B.TSN, B.TSO, B.CJ, B.FJDID, B.SXRQ, B.FLAG, B.SXZT, B.WHSJ, B.DPRTCODE, B.SHZH, B.XKZH, B.PCH, D.FJJX, (CASE WHEN D2.ZJH IS NULL THEN D4.ZWMS ELSE D2.ZWMS END) AS ZJHMS FROM (
select B.*, 1 flag from B_S_003 B where b.id = #{paramsMap.cxjid,jdbcType=VARCHAR}
and (UPPER(b.jh) like UPPER('%'||#{keyword}||'%') or UPPER(b.xlh) like UPPER('%'||#{keyword}||'%') or UPPER(b.nbsbm) like UPPER('%'||#{keyword}||'%') or UPPER(b.zjh) like UPPER('%'||#{keyword}||'%') or UPPER(b.cjjh) like UPPER('%'||#{keyword}||'%') or UPPER(b.zwmc) like UPPER('%'||#{keyword}||'%') or UPPER(b.ywmc) like UPPER('%'||#{keyword}||'%') )
and b.wz = #{wz}
union all
select B.*, 2 flag from (select * from B_S_003 B
AND B.BJLX = 1 AND B.FJDID != '0') B ) B LEFT JOIN D_007 D ON B.FJZCH = D.FJZCH AND B.DPRTCODE = D.DPRTCODE LEFT JOIN D_008 D3 ON B.JH = D3.BJH AND B.DPRTCODE = D3.DPRTCODE AND D3.ZT = 1 LEFT JOIN D_005 D2 ON D3.ZJH = D2.ZJH AND D3.DPRTCODE = D2.DPRTCODE LEFT JOIN D_005 D4 ON D4.ZJH = B.ZJH AND D4.DPRTCODE = B.DPRTCODE
ORDER BY B.FLAG ,B.KBS, B.CJ, B.JH, B.XLH, B.NBSBM
ORDER BY B.${pagination.sort} ${pagination.order}

select B2.jh, B2.xlh, sum(B2.fxsj_xs) fxsjXs, sum(B2.fxsj_fz) fxsjFz, sum(B2.fsj_xs) fsjXs, sum(B2.fsj_fz) fsjFz, sum(B2.qljxh) qljxh, sum(B2.jcsj_xs) jcsjXs, sum(B2.jcsj_fz) jcsjFz, sum(B2.jcxh) jcxh, sum(B2.fdj_n1) fdjN1, sum(B2.fdj_n2) fdjN2, sum(B2.fdj_n3) fdjN3, sum(B2.fdj_n4) fdjN4, sum(B2.fdj_n5) fdjN5, sum(B2.fdj_n6) fdjN6, sum(B2.ssdsj_xs) ssdsjXs, sum(B2.ssdsj_fz) ssdsjFz, sum(B2.dgxh) dgxh, sum(B2.ts1) ts1, sum(B2.ts2) ts2 from b_h_01001 B2
INNER JOIN B_S_006 B3 ON B3.ID = #{fxjldid,jdbcType=VARCHAR}
WHERE B2.IS_CYJS = 1 AND B2.ZT = 1 AND B2.XDBS = 0 AND B2.JH = #{jh,jdbcType=VARCHAR} AND B2.XLH = #{xlh,jdbcType=VARCHAR} AND B2.DPRTCODE = #{dprtcode,jdbcType=VARCHAR}
AND (B2.CSBJ IN (1, 2) OR (B2.FXRQ < B3.FXRQ) OR (B2.FXRQ = B3.FXRQ AND B2.FXJLDCJSJ < B3.ZDSJ))
GROUP BY B2.JH, B2.XLH

    select  from b_s_003 where ZT=1 AND  xlh is not null and dprtcode=#{dprtcode,jdbcType=VARCHAR} and fjzch in (
		select fjzch from d_007 where dprtcode=#{dprtcode,jdbcType=VARCHAR} and fjjx =#{fjjx,jdbcType=VARCHAR}
		)
