select b1.FJZCH, b1.DPRTCODE, b1.FJJX, b1.XLH, b1.BZM, b1.CCRQ, b1.JD, b1.JSGZJL, b1.BZ, b1.BMID, b1.CJRID, b1.CJSJ, b1. ZT, b1.GJDJZH, b1.gjdjzjkrq, b1.gjdjzfjid, b1.SHZH, b1.shzjkrq, b1.shzfjid, b1.WXDTXKZH, b1.DTZZJKRQ, b1.wxdtzzfjid, CEIL(to_date(to_char(b1.dtzzjkrq,'yyyy-mm-dd'),'yyyy-mm-dd') - CURRENT_DATE) syts, CEIL(to_date(to_char(b1.shzjkrq,'yyyy-mm-dd'),'yyyy-mm-dd') - CURRENT_DATE) syts1, CEIL(to_date(to_char(b1.gjdjzjkrq,'yyyy-mm-dd'),'yyyy-mm-dd') - CURRENT_DATE) syts2, b2.jdms from D_007 b1 left join d_012 b2 on b1.jd = b2.id
order by syts asc
order by ${pagination.sort} ${pagination.order}

