mapper.select:
SELECT B.ID AS ID, B.DPRTCODE AS DPRTCODE,
B.REC_CZSM AS REC_CZSM,
B.REC_CZLS AS REC_CZLS, B.REC_XGLX AS REC_XGLX,
RECWHR.USERNAME||' '||RECWHR.REALNAME AS REC_CZR_NAME, (CASE WHEN (B.PGDH IS NULL) THEN '机型 : '||B.JX ELSE B.PGDH||' R'||B.BB END )REC_HD, B.REC_CZSJ AS REC_CZSJ, B.REC_IP AS REC_IP
FROM B_G2_001_REC B LEFT JOIN T_USER RECWHR ON RECWHR.ID = B.REC_CZRID LEFT JOIN D_005 ZJ on (B.ZJH=ZJ.ZJH AND B.DPRTCODE = ZJ.DPRTCODE) WHERE 1=1
and B.REC_CZLS = #{paramsMap.REC_CZLS,jdbcType=VARCHAR}
and B.ID = #{paramsMap.ID,jdbcType=VARCHAR}
and B.DPRTCODE = #{paramsMap.DPRTCODE,jdbcType=VARCHAR}
and (UPPER(B.PGDH) like UPPER('%'||#{paramsMap.KEYWORD,jdbcType=VARCHAR}||'%'))
order by B.REC_CZSJ DESC
