SELECT B.* , WHR.USERNAME||' '||WHR.REALNAME WHR_REALNAME, WHR_REC.USERNAME||' '||WHR_REC.REALNAME WHR_REC_REALNAME, WHDW.DPRTNAME WHDW_DRPTNAME, ( CASE WHEN B.ZT = 0 THEN '无效' WHEN B.ZT = 1 THEN '有效' END ) ZT_NAME,B.KCBH AS REC_HD
FROM B_P_003_REC B LEFT JOIN T_USER WHR ON WHR.ID = B.WHRID LEFT JOIN T_USER WHR_REC ON WHR_REC.ID = B.REC_CZRID LEFT JOIN T_DEPARTMENT WHDW ON WHDW.ID = B.WHBMID WHERE 1 = 1
and B.REC_CZLS = #{paramsMap.REC_CZLS,jdbcType=VARCHAR}
and B.ID = #{paramsMap.ID,jdbcType=VARCHAR}
and B.DPRTCODE = #{paramsMap.DPRTCODE,jdbcType=VARCHAR}
and ( UPPER(B.KCBH) like UPPER('%'||#{paramsMap.KEYWORD,jdbcType=VARCHAR}||'%') )
order by B.REC_CZSJ DESC

SELECT B.* ,JG.DPRTNAME DPRTCODE_STR ,B.WJDX||'K' WJDX_STR ,CASE B.YXZT WHEN 1 THEN '有效' ELSE '无效' END YXZT_STR ,CZR.USERNAME ||' '|| CZR.REALNAME CZR_STR ,CZBM.DPRTNAME CZBM_STR ,REC_CZR.USERNAME ||' '|| REC_CZR.REALNAME REC_CZR_STR ,(CASE B.REC_XGLX WHEN 1 THEN '新增' WHEN 2 THEN '修改' WHEN 3 THEN '删除' ELSE '' END) REC_XGLX_STR ,'CURRENT' DATA_VERSION ,'' AS REC_HD FROM D_011_REC B LEFT JOIN T_USER REC_CZR ON REC_CZR.ID = B.REC_CZRID LEFT JOIN T_DEPARTMENT JG ON JG.ID = B.DPRTCODE LEFT JOIN T_USER CZR ON CZR.ID = B.CZRID LEFT JOIN T_DEPARTMENT CZBM ON CZBM.ID = B.CZBMID WHERE B.REC_ZBID = #{paramsMap.ID,jdbcType=VARCHAR} AND B.REC_CZLS = #{paramsMap.REC_CZLS,jdbcType=VARCHAR}
UNION ALL
SELECT B.* ,JG.DPRTNAME DPRTCODE_STR ,B.WJDX||'K' WJDX_STR ,CASE B.YXZT WHEN 1 THEN '有效' ELSE '无效' END YXZT_STR ,CZR.USERNAME ||' '|| CZR.REALNAME CZR_STR ,CZBM.DPRTNAME CZBM_STR ,REC_CZR.USERNAME ||' '|| REC_CZR.REALNAME REC_CZR_STR ,(CASE B.REC_XGLX WHEN 1 THEN '新增' WHEN 2 THEN '修改' WHEN 3 THEN '删除' ELSE '' END) REC_XGLX_STR ,'LAST' DATA_VERSION ,'' AS REC_HD FROM D_011_REC B LEFT JOIN T_USER REC_CZR ON REC_CZR.ID = B.REC_CZRID LEFT JOIN T_DEPARTMENT JG ON JG.ID = B.DPRTCODE LEFT JOIN T_USER CZR ON CZR.ID = B.CZRID LEFT JOIN T_DEPARTMENT CZBM ON CZBM.ID = B.CZBMID WHERE B.REC_ZBID = #{paramsMap.ID,jdbcType=VARCHAR} AND B.REC_CZLS IN ( SELECT REC_CZLS FROM ( SELECT ROW_NUMBER() OVER(ORDER BY REC_CZSJ DESC) RNO , REC_CZLS FROM B_P_003_REC A WHERE A.ID = #{paramsMap.ID,jdbcType=VARCHAR} AND A.REC_CZSJ <= ( SELECT C.REC_CZSJ FROM B_P_003_REC C WHERE C.ID= #{paramsMap.ID,jdbcType=VARCHAR} AND C.REC_CZLS = #{paramsMap.REC_CZLS,jdbcType=VARCHAR} ) AND A.REC_CZLS != #{paramsMap.REC_CZLS,jdbcType=VARCHAR} ) WHERE 1 = RNO )
