mapper.select:
SELECT B.* ,CZR.USERNAME ||' '|| CZR.REALNAME CZR_REALNAME ,'人员编号:'||B.RYBH REC_HD FROM B_Z_001_REC B LEFT JOIN T_USER CZR ON CZR.ID = B.REC_CZRID WHERE 1 = 1
and B.REC_CZLS = #{paramsMap.REC_CZLS,jdbcType=VARCHAR}
and B.ID = #{paramsMap.ID,jdbcType=VARCHAR}
and B.DPRTCODE = #{paramsMap.DPRTCODE,jdbcType=VARCHAR}
and ( UPPER(b.rybh) like UPPER('%'||#{paramsMap.KEYWORD,jdbcType=VARCHAR}||'%') or UPPER(b.xm) like UPPER('%'||#{paramsMap.KEYWORD,jdbcType=VARCHAR}||'%') or UPPER(b.lxdh) like UPPER('%'||#{paramsMap.KEYWORD,jdbcType=VARCHAR}||'%') or UPPER(b.yxdz) like UPPER('%'||#{paramsMap.KEYWORD,jdbcType=VARCHAR}||'%') or UPPER(b.yzbm) like UPPER('%'||#{paramsMap.KEYWORD,jdbcType=VARCHAR}||'%') or UPPER(b.zzmm) like UPPER('%'||#{paramsMap.KEYWORD,jdbcType=VARCHAR}||'%') )
order by B.REC_CZSJ DESC

SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY B.REC_CZSJ DESC) RNO ,B.* ,WHR.USERNAME ||' '|| WHR.REALNAME WHR_REALNAME ,CZR.USERNAME ||' '|| CZR.REALNAME CZR_REALNAME ,TO_CHAR(B.SR,'YYYY-MM-DD') SRSTR ,TO_CHAR(B.CJGZRQ,'YYYY-MM-DD') CJGZRQSTR ,TO_CHAR(B.RZRQ,'YYYY-MM-DD') RZRQSTR ,TO_CHAR(B.LZRQ,'YYYY-MM-DD') LZRQSTR ,(CASE WHEN B.ZZZT = 1 THEN '在职' WHEN B.ZZZT = 0 THEN '离职' END) ZZZTSTR ,(CASE WHEN B.WBBS = 1 THEN '内部' WHEN B.WBBS = 2 THEN '外部' END) WBBSSTR ,(CASE WHEN B.XB = 1 THEN '男' WHEN B.XB = 2 THEN '女' END) XBSTR ,(CASE WHEN B.IS_JH = 1 THEN '已婚' WHEN B.IS_JH = 0 THEN '未婚' END) ISJHSTR ,'人员编号:'||B.RYBH REC_HD ,(CASE WHEN B.REC_CZLS = #{paramsMap.REC_CZLS,jdbcType=VARCHAR} THEN 'CURRENT' ELSE 'LAST' END) DATA_VERSION
FROM B_Z_001_REC B LEFT JOIN T_USER CZR ON CZR.ID = B.REC_CZRID LEFT JOIN T_USER WHR ON WHR.ID = B.WHRID WHERE B.ID = #{paramsMap.ID,jdbcType=VARCHAR} AND (select rec_czsj from b_z_001_REC where REC_CZLS = #{paramsMap.REC_CZLS,jdbcType=VARCHAR} and ID = #{paramsMap.ID,jdbcType=VARCHAR}) >= B.rec_czsj
) WHERE 2 >=RNO
