mapper.select:
SELECT B.* ,WHR.USERNAME ||' '|| WHR.REALNAME CZR_REALNAME
,WHDW.DPRTNAME WHDW_DRPTNAME
, B.ypgg||' '||( CASE WHEN B.REC_CZSM = 0 THEN '创建' WHEN B.REC_CZSM = 1 THEN '提交' WHEN B.REC_CZSM = 2 THEN '已审核' WHEN B.REC_CZSM = 3 THEN '已批准' WHEN B.REC_CZSM = 4 THEN '中止（关闭）' WHEN B.REC_CZSM = 5 THEN '审核驳回' WHEN B.REC_CZSM = 6 THEN '批准驳回' WHEN B.REC_CZSM = 8 THEN '作废' WHEN B.REC_CZSM = 9 THEN '指定结束'
WHEN B.REC_CZSM = 10 THEN '完成' WHEN B.REC_CZSM = 11 THEN '撤销' WHEN B.REC_CZSM = 12 THEN '修改' WHEN B.REC_CZSM = 13 THEN '改版' WHEN B.REC_CZSM = 14 THEN '已生效' WHEN B.REC_CZSM = 15 THEN '提交生产确认' WHEN B.REC_CZSM = 16 THEN '借出确认' WHEN B.REC_CZSM = 17 THEN '归还' WHEN B.REC_CZSM = 18 THEN '导入' END ) REC_HD FROM b_j_005_REC B LEFT JOIN T_USER WHR ON WHR.ID = B.rec_czrid
LEFT JOIN T_DEPARTMENT WHDW ON WHDW.ID = B.rec_czrid WHERE 1=1
and B.REC_CZLS = #{czls,jdbcType=VARCHAR}
and B.ID = #{id,jdbcType=VARCHAR}
and B.DPRTCODE = #{paramsMap.DPRTCODE,jdbcType=VARCHAR}
and ( UPPER(B.ypgg) like UPPER('%'||#{paramsMap.KEYWORD,jdbcType=VARCHAR}||'%'))
order by B.REC_CZSJ DESC
