mapper.select:
SELECT B.ID ,B.REC_CZLS ,B.GDBH AS REC_HD ,B.REC_CZSM ,B.REC_CZSJ ,B.REC_IP ,WHR.USERNAME||' '||WHR.REALNAME WHR_REALNAME FROM B_S2_014_REC B LEFT JOIN T_USER WHR ON WHR.ID = B.WHRID WHERE 1=1
and B.DPRTCODE = #{paramsMap.DPRTCODE,jdbcType=VARCHAR}
and B.GDLX IN
#{item}
and ( UPPER(B.GDBH) like UPPER('%'||#{paramsMap.KEYWORD,jdbcType=VARCHAR}||'%') )
order by B.REC_CZSJ DESC,B.GDBH DESC

SELECT * FROM(
SELECT B.* ,'CURRENT' DATA_VERSION ,WHR_REC.USERNAME||' '||WHR_REC.REALNAME WHR_REC_REALNAME ,'工单管理' YWLX_NAME ,'' REC_HD FROM B_G2_999_REC B LEFT JOIN T_USER WHR_REC ON WHR_REC.ID = B.REC_CZRID WHERE B.YWID = #{paramsMap.ID,jdbcType=VARCHAR} AND B.REC_CZLS = #{paramsMap.REC_CZLS,jdbcType=VARCHAR}
UNION ALL
SELECT B.* ,'LAST' DATA_VERSION ,WHR_REC.USERNAME||' '||WHR_REC.REALNAME WHR_REC_REALNAME ,'工单管理' YWLX_NAME ,'' REC_HD FROM B_G2_999_REC B LEFT JOIN T_USER WHR_REC ON WHR_REC.ID = B.REC_CZRID WHERE B.REC_ZBID = #{paramsMap.ID,jdbcType=VARCHAR} AND B.REC_CZLS IN ( SELECT REC_CZLS FROM ( SELECT ROW_NUMBER() OVER(ORDER BY REC_CZSJ DESC) RNO , REC_CZLS FROM B_S2_014_REC A WHERE A.ID = #{paramsMap.ID,jdbcType=VARCHAR} AND A.REC_CZSJ <= ( SELECT C.REC_CZSJ FROM B_G2_013_REC C WHERE C.ID= #{paramsMap.ID,jdbcType=VARCHAR} AND C.REC_CZLS = #{paramsMap.REC_CZLS,jdbcType=VARCHAR} ) AND A.REC_CZLS != #{paramsMap.REC_CZLS,jdbcType=VARCHAR} ) WHERE 1 = RNO )
) A ORDER BY A.XC ASC

SELECT * FROM(
SELECT B.* ,'CURRENT' DATA_VERSION ,WHR_REC.USERNAME||' '||WHR_REC.REALNAME WHR_REC_REALNAME ,'工卡' YWLX_NAME ,(CASE WHEN B.IS_BJ = 0 THEN '否' WHEN B.IS_BJ = 1 THEN '是' END ) BJ_NAME ,'' REC_HD FROM B_G2_996_REC B LEFT JOIN T_USER WHR_REC ON WHR_REC.ID = B.REC_CZRID WHERE B.YWID = #{paramsMap.ID,jdbcType=VARCHAR} AND B.REC_CZLS = #{paramsMap.REC_CZLS,jdbcType=VARCHAR}
UNION ALL
SELECT B.* ,'LAST' DATA_VERSION ,WHR_REC.USERNAME||' '||WHR_REC.REALNAME WHR_REC_REALNAME ,'工卡' YWLX_NAME ,(CASE WHEN B.IS_BJ = 0 THEN '否' WHEN B.IS_BJ = 1 THEN '是' END ) BJ_NAME ,'' REC_HD FROM B_G2_996_REC B LEFT JOIN T_USER WHR_REC ON WHR_REC.ID = B.REC_CZRID WHERE B.REC_ZBID = #{paramsMap.ID,jdbcType=VARCHAR} AND B.REC_CZLS IN ( SELECT REC_CZLS FROM ( SELECT ROW_NUMBER() OVER(ORDER BY REC_CZSJ DESC) RNO , REC_CZLS FROM B_S2_014_REC A WHERE A.ID = #{paramsMap.ID,jdbcType=VARCHAR} AND A.REC_CZSJ <= ( SELECT C.REC_CZSJ FROM B_G2_013_REC C WHERE C.ID= #{paramsMap.ID,jdbcType=VARCHAR} AND C.REC_CZLS = #{paramsMap.REC_CZLS,jdbcType=VARCHAR} ) AND A.REC_CZLS != #{paramsMap.REC_CZLS,jdbcType=VARCHAR} ) WHERE 1 = RNO )
) A ORDER BY A.XC ASC
