mapper.select:
select t1.ID, t1.GWID, t1.WXRYDAID, t1.ZT, t1.WHBMID, t1.WHRID, t1.WHSJ, t1.FJJX,t1.LB,t1.KSRQ, t1.JZRQ,t2.szdw,t2.xm,t2.rybh,t2.wbbs from b_p_006 t1 left join b_z_001 t2 on t1.wxrydaid=t2.id
order by t2.wbbs asc,t2.szdw desc,t2.rybh desc
order by ${pagination.sort} ${pagination.order}

mapper.sql:
	   where  t1.zt=1

             and t1.dprtcode =  #{dprtcode}


             and t1.gwid =  #{gwid}


            and  t1.id  = #{id,jdbcType=VARCHAR}


		    and coalesce(to_date(to_char(t1.KSRQ, 'yyyy-mm-dd'), 'yyyy-mm-dd'),to_date('1111-11-11', 'yyyy-MM-dd')) <= to_date(to_char(sysdate, 'YYYYMMDD'), 'yyyy-MM-dd')
  			and coalesce(to_date(to_char(t1.JZRQ, 'yyyy-mm-dd'), 'yyyy-mm-dd'),to_date('9999-11-11', 'yyyy-MM-dd')) >= to_date(to_char(sysdate, 'YYYYMMDD'), 'yyyy-MM-dd')

mapper.select:
select 

from B_P_006
where wxrydaid = #{wxrydaid,jdbcType=VARCHAR} and gwid = #{gwid,jdbcType=VARCHAR}

mapper.delete:
delete from B_P_006
 where wxrydaid = #{wxrydaid,jdbcType=VARCHAR}
and gwid = #{gwid,jdbcType=VARCHAR}
and zt = #{zt,jdbcType=DECIMAL}

mapper.update:
update B_P_006
set 
  ZT = 1,
  KSRQ = #{ksrq,jdbcType=TIMESTAMP},
  JZRQ = #{jzrq,jdbcType=TIMESTAMP}
where ID = #{id,jdbcType=VARCHAR}

update B_P_006
set 
  ZT = 1,
  KSRQ = #{ksrq,jdbcType=TIMESTAMP},
  JZRQ = #{jzrq,jdbcType=TIMESTAMP}
where GWID = #{gwid,jdbcType=VARCHAR} and WXRYDAID = #{wxrydaid,jdbcType=VARCHAR}
