mapper.select:
select ,U3.USERNAME AS JUSERNAME,U3.REALNAME AS JREALNAME,DPRT.DPRTNAME from B_P_002 B JOIN B_P_003 BP003 ON B.KCID = BP003.ID LEFT JOIN T_USER U ON B.WHRID = U.ID left join T_USER U3 on B.ZDJSRID = U3.ID LEFT JOIN T_DEPARTMENT DPRT ON DPRT.ID = B.DPRTCODE where B.ZT in(1,10)
order by B.jh_ksrq desc,BP003.KCBH asc
order by ${pagination.sort} ${pagination.order}

select t1.id, case when t5.wxryid = #{jsid, jdbcType=VARCHAR} then 2 when t2.wxryid = #{jsid, jdbcType=VARCHAR} then 1 else 0 end TZLX, t3.id as cid, t3.kcbh as CKCBH, t3.kcmc as CKCMC, t3.fjjx, t1.jh_ksrq, t1.jh_kssj, t1.jh_jsrq, t1.jh_jssj, t1.kcdd, t1.pxlb, t1.pxjg, t1.zrr, t1.pxdx, t1.jsxm, t1.fxbs, t1.pxxs, t1.ksxs, t1.ks, t1.whsj, t1.dprtcode, t1.zt, t1.jsid, D011.ATTACHCOUNT from b_p_002 t1 join b_p_003 t3 on t1.kcid = t3.id and t3.zt = 1 left JOIN (SELECT MAINID,COUNT(1) AS ATTACHCOUNT FROM D_011 GROUP BY MAINID) D011 ON D011.MAINID = t3.ID left join (select b_p_00201.pxjhid, b_z_001.wxryid from b_p_00201 join b_z_001 on b_p_00201.zt = 1 and b_p_00201.is_yc = 1 and b_z_001.id = b_p_00201.wxrydaid and b_z_001.wxryid = #{jsid, jdbcType=VARCHAR}) t2 on t1.id = t2.pxjhid left join b_p_008 t5 on t1.jsid = t5.id where t1.dprtcode = #{dprtcode, jdbcType=VARCHAR} and (t5.wxryid = #{jsid, jdbcType=VARCHAR} or t1.whrid = #{jsid, jdbcType=VARCHAR} or t2.wxryid = #{jsid, jdbcType=VARCHAR}) and t1.zt = 1
order by jh_ksrq desc
order by ${pagination.sort} ${pagination.order}
